    private async Task<IEnumerable<ListCustomerDto>> SearchCustomer(string value)
    {
        await Task.Delay(5);

        if (string.IsNullOrEmpty(value))
        {
            return _customers;
        }
        else
        {
            return _customers.Where(x => x.FirstName.Contains(value, StringComparison.InvariantCultureIgnoreCase));
        }
    }

